#!/usr/bin/env python3# -*- coding: utf-8 -*-"""Created on Tue Dec 18 09:41:10 2018@author: joke"""from Shapes import *from pylab import random as rclass MovingShape:    def __init__(self, frame, shape, diameter):        self.shape = shape        self.diameter = diameter        self.figure = Shape(shape, diameter)        # Initialising X and Y        '''        The initial state is half the diameter to have them         positioned inside the frame.        '''        self.x = self.diameter / 2        self.y = self.diameter / 2        # Random moving direction and velocity##        self.dx = 4 # Test values##        self.dy = 3 # Test values        self.dx = 10 * r()        self.dy = 10 * r()                if (r() < 0.5):            self.dx = self.dx * -1         else:             self.dy = self.dy * -1                # Start position        self.minx = self.diameter / 2        self.maxx = frame.width - self.minx        self.miny = self.diameter / 2        self.maxy = frame.height - self.miny        # X and Y        #        self.x = self.minx # Test values#        self.y = self.miny # Test values        print('init {} {}'.format(round(self.x), round(self.y)))        self.x = self.minx + r() * (self.maxx - self.minx)        self.y = self.miny + r() * (self.maxy - self.miny)                print('{} {} {} {}'.format(round(self.minx), round(self.miny), self.maxx, self.maxy))                        self.goto(self.x, self.y)            def goto(self, x, y):        self.figure.goto(x, y)        class Square(MovingShape):    def __init__(self, frame, diameter):        MovingShape.__init__(self, frame, 'square', diameter)        